// Dashboard.js
import React from 'react';
import ImageComponent from './ImageComponent';

const Dashboard = ({ images, setImages }) => {
  // Create an array of placeholders with their corresponding widths
  const placeholders = [
    { width: '100%', height: '150px' },           // 1st row, full width
    { width: '50%', height: '150px' },            // 2nd row, 1st placeholder
    { width: '50%', height: '150px' },            // 2nd row, 2nd placeholder
    { width: '50%', height: '150px' },            // 3rd row, 1st placeholder
    { width: '50%', height: '150px' },            // 3rd row, 2nd placeholder
    { width: '50%', height: '150px' },            // 3rd row, 3rd placeholder
    { width: '100%', height: '150px' },           // Final row, full width
  ];

  // Function to render placeholders and images
  const renderDashboardItems = () => {
    return placeholders.map((placeholder, index) => {
      // Check if there is an image for the current placeholder
      const image = images[index];

      return (
        <div
          key={index}
          style={{
            width: placeholder.width,
            height: placeholder.height,
            border: '1px solid #ddd',
            margin: '5px',
            overflow: 'hidden',
            display: 'inline-block',
          }}
        >
          {image ? (
            <ImageComponent
              imageUrl={image.imageUrl}
              altText={image.altText}
              width="100%"
              height="100%"
            />
          ) : (
            <div style={{ textAlign: 'center', paddingTop: '50px' }}>
              Placeholder {index + 1}
            </div>
          )}
        </div>
      );
    });
  };

  return (
    <div style={{ marginTop: '20px' }}>
      <h2>Dashboard</h2>
      <div style={{ textAlign: 'center' }}>{renderDashboardItems()}</div>
    </div>
  );
};

export default Dashboard;
