// Dashboard.js
import React, { useEffect } from 'react';
import { useDrop } from 'react-dnd';
import ImageComponent from './ImageComponent';

const Dashboard = ({ images, setImages }) => {
  // Drop zone setup
  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'IMAGE',
    drop: (item) => handleDrop(item),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));

  const handleDrop = (item) => {
    const newImages = [...images, item];
    setImages(newImages);
  };

  useEffect(() => {
    // Save configuration to localStorage
    localStorage.setItem('dashboardConfig', JSON.stringify(images));
  }, [images]);


  const containerStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    height: '500px', // Fixed height for the drop zone
    width: '300px', // Allow the container to stretch to the full width of its parent
    margin: '0 auto', // Center the container horizontally
  };

  const imageStyle = {
    maxWidth: '100%',
    maxHeight: '100%',
    objectFit: 'contain',
    flex: '1',
    width: '100%', // Set width to 100% to make it adjust within the container
    height: '100', // Allow the height to adjust proportionally
    margin: '8px', // Adjust margin as needed
  };

  return (
    <div ref={drop} style={containerStyle}>
      <div >
        {images.map((image, index) => (
          <ImageComponent key={index} imageUrl={image.imageUrl} altText={image.altText} style={imageStyle}/>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
