// Dashboard.js
import React, { useEffect } from 'react';
import ImageComponent from './ImageComponent';

const Dashboard = ({ images, setImages }) => {
  const placeholders = [
    { gridArea: '1 / 1 / 2 / 2' },   // 1st row, full width
    { gridArea: '2 / 1 / 4 / 2' },   // 2nd row, 1st placeholder
    { gridArea: '2 / 2 / 4 / 3' },   // 2nd row, 2nd placeholder
    { gridArea: '4 / 1 / 7 / 2' },   // 3rd row, 1st placeholder
    { gridArea: '4 / 2 / 7 / 3' },   // 3rd row, 2nd placeholder
    { gridArea: '4 / 3 / 7 / 4' },   // 3rd row, 3rd placeholder
    { gridArea: '7 / 1 / 8 / 2' },   // Final row, full width
  ];

  useEffect(() => {
    console.log('Images have changed:', images);
  }, [images]);

  const renderDashboardItems = () => {
    return placeholders.map((placeholder, index) => {
      const image = images[index];

      return (
        <div
          key={index}
          style={{
            border: '1px solid #ddd',
            margin: '5px',
            overflow: 'hidden',
            display: 'inline-block',
            gridArea: placeholder.gridArea,
          }}
        >
          {image ? (
            <ImageComponent
              imageUrl={image.imageUrl}
              altText={image.altText}
              width="100%"
              height="100%"
            />
          ) : (
            <div style={{ textAlign: 'center', paddingTop: '50px' }}>
              Placeholder {index + 1}
            </div>
          )}
        </div>
      );
    });
  };

  return (
    <div style={{ marginTop: '20px', display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)' }}>
      <h2>Dashboard</h2>
      <div></div>
    </div>
  );
};

export default Dashboard;
