// App.js
import React, { useState } from 'react';
import Dashboard from './DashBoard';
import ImageUploader from './ImageUploader';

function App() {
  const [dashboardImages, setDashboardImages] = useState([]);

  const handleImageUpload = (image) => {
    // Add the uploaded image to the dashboardImages state
    setDashboardImages([...dashboardImages, image]);
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h1>Customizable Dashboard</h1>
      <ImageUploader onImageUpload={handleImageUpload} />
      <Dashboard images={dashboardImages} setImages={setDashboardImages} />
    </div>
  );
}

export default App;
