// Dashboard.js
import React, { useEffect } from 'react';
import { useDrop } from 'react-dnd';
import ImageComponent from './ImageComponent';

const Dashboard = ({ images, setImages }) => {
  // Drop zone setup
  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'IMAGE',
    drop: (item) => handleDrop(item),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));

  const handleDrop = (item) => {
    const newImages = [...images, item];
    setImages(newImages);
  };

  useEffect(() => {
    // Save configuration to localStorage
    localStorage.setItem('dashboardConfig', JSON.stringify(images));
  }, [images]);

  // Determine the maximum width for each row (2 images per row)
  const maxWidthPerRow = '300px'; // Adjust this value as needed

  // Apply a dynamic style to the container div
  const containerStyle = {
    height: '500px', // Fixed height for the drop zone
    maxWidth: '300px', // Allow the container to stretch to the full width of its parent
    margin: '0 auto', // Center the container horizontally
  };

  // Apply a dynamic style to each row div

  return (
    <div ref={drop} style={containerStyle}>
      <div style={rowStyle}>
        {images.map((image, index) => (
          <ImageComponent key={index} imageUrl={image.imageUrl} altText={image.altText} />
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
