// Dashboard.js
import React, { useEffect } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import ImageComponent from './ImageComponent';

const Dashboard = ({ images, setImages }) => {
  const [, drop] = useDrop(() => ({
    accept: 'IMAGE',
  }));

  useEffect(() => {
    // Save configuration to localStorage
    localStorage.setItem('dashboardConfig', JSON.stringify(images));
  }, [images]);

  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    height: '500px',
    margin: '0 auto',
    width: '60%',
  };

  const imagesContainer = {
    flex: 1,
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
    gridGap: '2px',
  };

  const imageStyle = {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  };

  const handleSwap = (dragIndex, dropIndex) => {
    const newImages = [...images];
    [newImages[dragIndex], newImages[dropIndex]] = [newImages[dropIndex], newImages[dragIndex]];
    setImages(newImages);
  };

  return (
    <div ref={drop} style={containerStyle}>
      <div style={imagesContainer}>
        {images.map((image, index) => (
          <DraggableImage
            key={index}
            index={index}
            imageUrl={image.imageUrl}
            altText={image.altText}
            style={imageStyle}
            handleSwap={handleSwap}
          />
        ))}
      </div>
    </div>
  );
};

const DraggableImage = ({ index, imageUrl, altText, style, handleSwap }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'IMAGE',
    item: { index },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      style={{ ...style, opacity: isDragging ? 0.5 : 1, cursor: 'move' }}
      onClick={() => console.log('Clicked on image:', index)} // Add your click handler here
    >
      <ImageComponent imageUrl={imageUrl} altText={altText} />
    </div>
  );
};

export default Dashboard;
